mapper.insert:
insert into project(
id, owner_id, status_no, text, start_date,
duration, end_date, parent, progress, open1, 
contents, createdAt, display_order
) 
values (
project_seq.nextval, #{owner_id}, #{status_no},#{text}, #{start_date},
#{duration}, #{end_date}, #{parent}, 0, 1, 
#{contents}, sysdate, #{display_order}
)

mapper.delete:
delete from project
WHERE id in (
	SELECT id FROM PROJECT p
	START WITH id = #{id}
	CONNECT BY PRIOR id = PARENT 
)

mapper.select:
SELECT o.id, member_no , title, start1, end1, content, BACKGROUNDCOLOR, m.name, m.DEPTNO, allday, textColor FROM OWNSCHEDULE o, MEMBER06 m WHERE 1 = 1 AND m.NO = o.MEMBER_NO
AND m.no = #{member_no}
union all SELECT p.id id, m.no member_no, text title, start_date start1, end_date end1, contents content, background BACKGROUNDCOLOR, m.name, m.DEPTNO , 1 as allday, '#ffffff' as textColor FROM PROJECT p, MEMBER06 m , STATUS s where 1 = #{visibility} AND m.NO(+) = p.OWNER_ID AND s.NO = p.STATUS_NO
AND m.no = #{member_no}

SELECT CEIL(avg(progress)*100) 
FROM project 
WHERE PARENT=#{id} OR id=#{id}
